package com.example.hp.androidrhcolonial.MenuVH;import android.support.v7.widget.RecyclerView;import android.view.View;import android.widget.TextView;import com.example.hp.androidrhcolonial.Interface.ItemClickListener;import com.example.hp.androidrhcolonial.R;/** * Created by HP on 20/04/2018. */public class OrdenVH extends RecyclerView.ViewHolder implements View.OnClickListener{    public TextView textOrdenId,txtOrdenEstatus,txtOrdenTelefono,txtordenDireecion;    private ItemClickListener itemClickListener;    public OrdenVH(View itemView) {        super(itemView);        txtordenDireecion=(TextView)itemView.findViewById(R.id.ordendireccion);        textOrdenId=(TextView)itemView.findViewById(R.id.ordenid);        txtOrdenEstatus=(TextView)itemView.findViewById(R.id.ordentestatus);        txtOrdenTelefono=(TextView)itemView.findViewById(R.id.ordentelefono);       itemView.setOnClickListener(this);    }    public void setItemClickListener(ItemClickListener itemClickListener) {        this.itemClickListener = itemClickListener;    }    @Override    public void onClick(View view) {      itemClickListener.onClick(view,getAdapterPosition(),false);    }}